mapper.update:
update ground
set status="cancelled"
where id = #{groupId}

mapper.select:
select g.*, category_id
from ground g join lecture l on
g.lecture_id = l.id

    AND l.category_id = #{categoryId}
    AND level = #{level}


    limit #{startRow}, #{ROWS_PER_PAGE}

select title, information, lecture_id as lectureId, level, max_capacity as maxCapacity, deposit, is_validated as isValidated, is_premium as isPremium, created_at as createdAt, start_at as startAt, end_at as endAt,validated_at as validatedAt, status, mission_fail_limit as missionFailLimit, u.id as userGroundId, user_id as userId, ground_id as groundId, is_attending as isAttending, is_success as isSuccess, rating, comment from ground g join user_ground u on g.id = u.ground_id
u.user_id = #{userId} AND g.status = #{status}
limit #{startRow}, #{ROWS_PER_PAGE}
